SQLStoredProcedureSpec.CreateProcedureSQL:
		
            create procedure dbo.USR_USP_UPDATEBATCHNUMBERBYSPONSORSHIPID
            (
                @ID uniqueidentifier,
                @BATCHNUMBER nvarchar(100)
            )
            as
            begin
	            declare @REVENUEID uniqueidentifier,
		                @CURRENTDATE datetime,
		                @CHANGEAGENTID uniqueidentifier;

                set @CURRENTDATE = getdate();

                exec USP_CHANGEAGENT_GETORCREATECHANGEAGENT @CHANGEAGENTID output

                select	
	                top 1
		                @REVENUEID = FTLI.FINANCIALTRANSACTIONID
                from
	                SPONSORSHIP S
	                inner join FINANCIALTRANSACTIONLINEITEM FTLI on S.REVENUESPLITID = FTLI.ID
                where
	                S.ID = @ID;
	
                if exists(select 1 from REVENUE_EXT where ID = @REVENUEID)
                begin
	                update
		                REVENUE_EXT
	                set
		                BATCHNUMBER = @BATCHNUMBER,
		                CHANGEDBYID = @CHANGEAGENTID,
		                DATECHANGED = @CURRENTDATE
	                where
		                ID = @REVENUEID;
                end
                else
                begin
	                insert into
		                REVENUE_EXT
		                (
			                ID,
			                BATCHNUMBER,
			                ADDEDBYID,
			                CHANGEDBYID
		                )
	                values
		                (
			                @REVENUEID,
			                @BATCHNUMBER,
			                @CHANGEAGENTID,
			                @CHANGEAGENTID
		                )	
                end
            end
